

class Solution(object):

    def findSubstring(self, s, words):
        """
        :type s: str
        :type words: List[str]
        :rtype: List[int]
        """

        if not s or not words or len(s) < len(words)*len(words[0]):
            return []
        wl = len(words[0])
        wn = len(words)
        result = []
        dic = {}
        for word in words:
            if word not in dic:
                dic[word] = 1
            else:
                dic[word] += 1
        
        for i in xrange(wl):
            subdic = {}
            while i <= len(s) - wl*wn:
                if  subdic == {}:
                    j = i
                    while j < i+wl*wn:
                        if s[j:j+wl] not in subdic:
                            subdic[s[j:j+wl]] = 1
                        else:
                            subdic[s[j:j+wl]] += 1
                        j += wl
                else:
                    subdic[s[i:i+wl]] -= 1
                    if subdic[s[i:i+wl]] == 0:
                        del subdic[s[i:i+wl]]

                    if s[j:j+wl] not in subdic:
                        subdic[s[j:j+wl]] = 1
                    else:
                        subdic[s[j:j+wl]] += 1
                    i += wl
                    j += wl
                if subdic == dic:
                    result.append(i)
                
        return result


