# 1-29
class Solution(object):
    def findSubstring(self, s, words):
        """
        :type s: str
        :type words: List[str]
        :rtype: List[int]
        """
        if not words or not s:
            return

        dic = {}
        res = []
        for word in words:
            if word not in dic:
                dic[word] = 1
            else:
                dic[word] += 1
        wl = len(words[0])
        wn = len(words)
        for i in xrange(wl):
            dic2 = {}
            while i <= len(s) - wl*wn:
                if dic2 == {}:
                    j = i
                    while  j<i+wl*wn:
                        if s[j:j+wl] not in dic:
                            i += wl
                            dic2 = {}
                            break
                        else:
                            if s[j:j+wl] not in dic2:
                                dic2[s[j:j+wl]] = 1
                            else:
                                dic2[s[j:j+wl]] += 1
                            j += wl
                else:
                    dic2[s[i:i+wl]] -= 1
                    if s[j:j+wl] not in dic:
                        i += wl
                        dic2 = {} 
                    else:
                        if s[j:j+wl] not in dic2:
                                dic2[s[j:j+wl]] = 1
                        else:
                            dic2[s[j:j+wl]] += 1
                    j += wl
                    i += wl
                if dic == dic2:
                    res.append(i)
        return res