class Solution(object):
    def isMatch(self, s, p):
        """
        :type s: str
        :type p: str
        :rtype: bool
        """
        m = len(s)
        n = len(p)

        dp = [[False]*(n+1) for _ in xrange(m+1)]

        dp[0][0] = True
        for j in xrange(n):
            if p[j] == '*':
                dp[0][j+1] = dp[0][j]
        for i in xrange(m):
            for j in xrange(n):
                if p[j] != '*':
                    if dp[i][j] and (s[i] == p[j] or p[j] == "?"):
                        dp[i+1][j+1] = True
                else:
                    if dp[i][j]:
                        dp[i][j+1] = True
                    if dp[i][j+1]:
                        dp[i+1][j+1] = True
                    if dp[i+1][j]:
                        dp[i+1][j+1] = True

        return dp[m][n]

