# 1-30
class Solution(object):
    def isMatch(self, s, p):
        """
        :type s: str
        :type p: str
        :rtype: bool
        """
        m = len(s)
        n = len(p)
        if len(p) - p.count('*') > m:
            return False

        dp = [[False]*(n+1) for _ in xrange(m+1)]
        
        dp[0][0] = True
        for i in xrange(n):
            dp[0][i+1] = dp[0][i] and p[i] == '*'
        
        for i in xrange(m):
            for j in xrange(n):
                if p[j] != '*':
                    dp[i+1][j+1] = dp[i][j] and (s[i] == p[j] or p[j] == '?')
                else:
                    dp[i+1][j+1] = dp[i][j+1] or dp[i+1][j] or dp[i][j]
                    
        return dp[m][n]

