# 1-20
class Solution(object):
    def exist(self, board, word):
        """
        :type board: List[List[str]]
        :type word: str
        :rtype: bool
        """
        if not word:
            return True
        if not board:
            return False
        board = [list(s) for s in board]

        m = len(board)
        n = len(board[0])
        for i in xrange(m):
            for j in xrange(n):
                if helper(board,word,i,j):
                    return True
        return False

def helper(board,word,i,j):
    if word[0] == board[i][j]:
        if not word[1:]:
            return True
        board[i][j] = ""
        if i>0 and helper(board,word[1:],i-1,j):
            return True
        if i<len(board)-1 and helper(board,word[1:],i+1,j):
            return True
        if j>0 and helper(board,word[1:],i,j-1):
            return True
        if j < len(board[0])-1 and helper(board,word[1:],i,j+1):
            return True
        board[i][j] = word[0]
        return False
    else:
        return False